def drawTextSize16(img, pos, text, color):
    x = pos[0]
    y = pos[1]
    bearing_x = 2
    space_size = 5
    for c in text:
        size = 0
        if str(ord(c)) in smallFont16:
            bitmap = smallFont16[str(ord(c))]
            for cy in range(0, 16):
                row = bitmap[cy]
                for cx in range(0, 16):
                    if row & (0b1 << cx):
                        img.putpixel((cx - bearing_x + x, cy + y), color)
                        size = max(size, cx - bearing_x)
        x = x + size + 2 if ord(c) != 32 else x + space_size


